from turtle import Turtle, Screenimport pandas as pdimport turtle as tdef states_game():    screen = Screen()    # Construct the path to the image    screen.title("Name the States")    state_list = pd.read_csv("US_states_game/50_states.csv")    screen.bgpic('US_states_game/blank_states_img.gif')    screen.tracer(0)    score = 0        state_writer = Turtle()    state_writer.hideturtle()    state_writer.penup()    states =list(state_list.state)            game_on = True        while game_on:        state_inp = screen.textinput(f"{score}/50 States Correct", "What's another state name?").title()            if state_inp =='Exit':            game_on = False                    if state_inp in states:            state_row = state_list[state_list.state == state_inp]            pos_state = (state_row.iloc[0][1], state_row.iloc[0][2])            state_writer.goto(pos_state)            state_writer.write(state_inp, align="center", font = ('Arial', 14, 'normal'))            score +=1            screen.update()                            screen.exitonclick(close_turtle)            def close_turtle():    t.bye()  # Close the Turtle graphics window without logs